import React, { useEffect, useState, useRef } from 'react';
import ChatBar from './ChatBar';
import ChatBody from './ChatBody';
import ChatFooter from './ChatFooter';

const ChatPage = ({ socket, onRouteChange, username }) => {
    const [messages, setMessages] = useState([]);  
    const [typingStatus, setTypingStatus] = useState('');
    const lastMessageRef = useRef(null); 

    async function getChatHistory() {
        try {
            const response = await fetch('http://localhost:4000/chathistory', {
                method: 'get',
                // headers: {'Content-Type': 'application/json'},
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const chatHistory = await response.json();
            const chatHistorySorted = chatHistory.sort((a, b) => {
                // Sort the array of messages (in json) by the message's time
                const timeA = new Date(a.time);
                const timeB = new Date(b.time);
                return timeA - timeB;
              });
            const keysToInclude = ['name', 'message'];
            const result = chatHistorySorted.map(({name, message}) => ({name, message}));
            console.log(result);
            return result;
        } catch (error) {
            console.error('Error fetching chat history:', error);
        }
    }

    getChatHistory()
    .then(chatHistory => {
        console.log(chatHistory.length);
        setMessages(chatHistory);
    })
    .catch(err => console.error("Error"))

    useEffect(() => {
        socket.on('messageResponse', (data) => setMessages([...messages, data]));
      }, [socket, messages]);

    console.log("the current messages: ", messages);
    // useEffect(() => {
    // socket.on('messageResponse', (data) => setMessages([...messages, data]));
    // }, [socket, messages]);

    useEffect(() => {
    // scroll to bottom every time messages change
    lastMessageRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    return (
        <div className="chat">
        {/* <ChatBar socket={socket} /> */}
        <div className="chat__main">
            <ChatBody messages={messages} lastMessageRef={lastMessageRef} onRouteChange={onRouteChange} username={username} socket={socket}/>
            <ChatFooter socket={socket} username={username}/>
        </div>
        </div>
    );
};

export default ChatPage;
