import React from "react";
import './ChatWindow.css';

//chat-history table: name, message, time, upvote, downvote

class ChatWindow extends React.Component {
    constructor(props) {
        super(props);
        // console.log(super.state.chatHistory);
        this.state = {
            chatHistory: this.loadHistory(),
        }
        // this.loadHistory();
        // console.log(this.state.chatHistory);
    }

    // loadHistory = () => {
    //     fetch('http://localhost:3000/chathistory', {
    //             method: 'get',
    //             // headers: {'Content-Type': 'application/json'},
    //         })
    //             .then(response => response.json())
    //             .then(chatHistory => {

    //                 this.setState(Object.assign(this.state.chatHistory, chatHistory));
    //             })
    //             .catch(console.log)
    // }
    loadHistory = async () => {
        try {
            const response = await fetch('http://localhost:3000/chathistory', {
                method: 'get',
                // headers: {'Content-Type': 'application/json'},
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const chatHistory = await response.json().body;
            console.log(chatHistory);
            return chatHistory;
        } catch (error) {
            console.error('Error fetching chat history:', error);
        }
    };

    onNewMessage = (event) => {
        console.log(event.target.value);
    }

    onButtonSend = (event) => {
        console.log(event.target.value);
    }

    render() {
        return (
            // <div>
            //     <p className="f3">
            //         {'This is Chat Room'}
            //     </p>

            //     <div className='center'>
            //         <div className='form center pa4 br3 shadow-5'>
            //         <input className='f4 pa2 w-70 center' type='tex' onChange={onInputChange}/>
            //         <button
            //             className='w-30 grow f4 link ph3 pv2 dib white bg-light-purple'
            //             onClick={onButtonSubmit}
            //         >Detect</button>
            //         </div>
            //     </div>
            // </div>
            <div>
                <div>
                    <meta name="viewport" content="width=device-width,initial-scale=1.0"/>
                    <title>Chat Window</title>
                    {this.chatHistory}
                    {/* {`Previous message: ${this.state.chatHistory[0].name}`} */}
                </div>

                <body>
                    <ul id="messages"></ul>
                    <div id="form" action="">
                    <input 
                        id="input" 
                        autocomplete="off" 
                        value="Type Message Here"
                        onChange={this.onNewMessage}
                    />
                    {/* <input 
                            onClick={this.onButtonSend}
                            className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
                            type="submit" 
                            value="Send"
                    /> */}
                    <button
                        className='w-10 grow f6 link ph3 pv2 dib white bg-light-purple'
                        onClick={this.onButtonSend}
                    >Send</button>
                    </div>
                </body>
            </div>
        );
    }

}

export default ChatWindow;
